#include <iostream>
#include <string>

std::string solve(int A) {
    std::string res;
    int n = A / 2;
    int m = A - n;
    for (int i = 0; i < n; i++) {
        res += '(';
    }
    for (int i = 0; i < m; i++) {
        res += ')';
    }
    for (int i = 0; i < n - 1; i++) {
        res += '(';
        res += ')';
    }
    return res;
}

int main() {
    int A;
    std::cin >> A;
    std::cout << solve(A) << std::endl;
    return 0;
}
