import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int A = scanner.nextInt();
        StringBuilder result = new StringBuilder();
        int n = 0;
        int m = 0;
        int k = 0;

        while (A > 0) {
            if (A % 2 == 0) {
                n++;
                A /= 2;
            } else {
                m++;
                A--;
                k++;
            }
        }

        for (int i = 0; i < n; i++) {
            result.append("(");
        }
        for (int i = 0; i < m; i++) {
            result.append(")");
        }
        for (int i = 0; i < k; i++) {
            result.insert(0, "(");
            result.append(")");
        }
        System.out.println(result);
    }
}
