def generate_string(n):
    if n % 2 == 0:
        return "()" * (n // 2)
    else:
        result = "()" * ((n - 5) // 2) + ")(" + "()" * ((n - 1) // 2)
        return result

def main():
    n = int(input())
    print(generate_string(n))

if __name__ == "__main__":
    main()
