#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 100000

typedef struct {
    int num;
    int parent;
    int child;
    int depth;
    int size;
    int similar;
} Node;

Node nodes[N];
int num_nodes;

void dfs(int node) {
    nodes[node].depth = 0;
    nodes[node].size = 1;
    nodes[node].similar = 1;
    nodes[node].parent = -1;
    nodes[node].child = -1;

    for (int i = nodes[node].child; i != -1; i = nodes[i].child) {
        dfs(i);
        nodes[node].size += nodes[i].size;
        nodes[node].similar *= nodes[i].similar;
    }
}

int main() {
    int n;
    scanf("%d", &n);
    num_nodes = n;

    for (int i = 1; i < n; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        nodes[b].parent = a;
        nodes[a].child = b;
    }

    dfs(1);

    long long ans = 0;
    for (int i = 2; i < n; i++) {
        if (nodes[i].similar == 1) {
            ans += nodes[i].size;
        }
    }

    printf("%lld\n", ans);

    return 0;
}
