#include <iostream>
#include <vector>
#include <unordered_map>

using namespace std;

const int N = 1e5 + 5;

int n, cnt, ans;
vector<int> G[N];
unordered_map<int, int> id;
int dep[N], siz[N], son[N], top[N], dfn[N], num, tot;
int f[N][20], d[N][20];

void dfs1(int u, int fa) {
    dep[u] = dep[fa] + 1;
    f[u][0] = fa;
    d[u][0] = dep[u];
    for (int v : G[u]) {
        if (v == fa) continue;
        dfs1(v, u);
        f[u][0] = v;
    }
}

void dfs2(int u, int tp) {
    top[u] = tp;
    dfn[u] = ++num;
    siz[u] = 1;
    for (int v : G[u]) {
        if (v == f[u][0]) continue;
        dfs2(v, tp);
        siz[u] += siz[v];
        if (siz[v] > siz[son[u]]) son[u] = v;
    }
}

void dfs3(int u, int fa) {
    if (u != 1) {
        for (int i = 0; i <= 18; i++) {
            f[u][i + 1] = f[f[u][i]][i];
            d[u][i + 1] = d[u][i] + d[f[u][i]][i];
        }
    }
    for (int v : G[u]) {
        if (v == fa || v == son[u]) continue;
        dfs3(v, u);
    }
}

int lca(int u, int v) {
    if (dep[u] < dep[v]) swap(u, v);
    for (int i = 18; i >= 0; i--) {
        if (dep[f[u][i]] >= dep[v]) u = f[u][i];
    }
    if (u == v) return u;
    for (int i = 18; i >= 0; i--) {
        if (f[u][i] != f[v][i]) {
            u = f[u][i];
            v = f[v][i];
        }
    }
    return f[u][0];
}

int get(int u, int v) {
    int res = 0;
    for (int i = 18; i >= 0; i--) {
        if (dep[f[u][i]] >= dep[v]) res += (1 << i), u = f[u][i];
    }
    return res;
}

int main() {
    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        G[u].push_back(v);
        G[v].push_back(u);
    }
    dfs1(1, 0);
    dfs2(1, 1);
    dfs3(1, 0);
    for (int i = 1; i <= n; i++) {
        if (i != 1) {
            int u = i, v = son[i];
            int l = lca(u, v);
            int res = get(u, l) + get(v, l) + 1;
            ans += (siz[son[i]] + 1) * (n - siz[son[i]] - 1) / 2;
            ans += (siz[son[i]] + 1) * (siz[son[i]] - 1) / 2;
            ans -= (res + 1) * (res - 1) / 2;
        }
    }
    cout << ans << endl;
    return 0;
}
