import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] a, b;
    static List<Integer>[] G;
    static int[] depth, subtreeSize, parent;
    static long[] dp;
    static long ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        a = new int[N];
        b = new int[N];
        G = new List[N];
        for (int i = 0; i < N; i++) {
            G[i] = new ArrayList<>();
        }
        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken()) - 1;
            b[i] = Integer.parseInt(st.nextToken()) - 1;
            G[a[i]].add(b[i]);
        }

        depth = new int[N];
        subtreeSize = new int[N];
        parent = new int[N];
        dp = new long[N];
        dfs(0, -1);

        for (int i = 0; i < N; i++) {
            dp[i] = 1;
            for (int j : G[i]) {
                dp[i] += dp[j];
            }
        }

        for (int i = 0; i < N; i++) {
            for (int j : G[i]) {
                if (i < j) {
                    if (dfs2(i, j)) {
                        ans++;
                    }
                }
            }
        }

        pw.println(ans);
        pw.close();
    }

    static void dfs(int node, int par) {
        parent[node] = par;
        depth[node] = depth[par] + 1;
        subtreeSize[node] = 1;
        for (int child : G[node]) {
            dfs(child, node);
            subtreeSize[node] += subtreeSize[child];
        }
    }

    static boolean dfs2(int i, int j) {
        if (subtreeSize[i] != subtreeSize[j]) {
            return false;
        }
        if (depth[i] != depth[j]) {
            return false;
        }
        if (dp[i] != dp[j]) {
            return false;
        }
        if (i == j) {
            return false;
        }
        if (parent[i] == j) {
            return dfs2(parent[i], j);
        }
        if (parent[j] == i) {
            return dfs2(parent[j], i);
        }
        return true;
    }
}
