#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 100005

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    int n, l, a[N], b[N], c[N];
    scanf("%d %d", &n, &l);
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &c[i]);
    }
    qsort(a, n, sizeof(int), compare);
    qsort(b, n, sizeof(int), compare);
    qsort(c, n, sizeof(int), compare);
    int day = 0, position = 0, sum = 0;
    while (day < n) {
        sum += a[day] - b[day];
        if (sum >= l) {
            printf("%d\n", day + 1);
            return 0;
        }
        position += a[day] - b[day];
        if (position >= c[day]) {
            printf("-1\n");
            return 0;
        }
        day++;
    }
    printf("-1\n");
    return 0;
}
