#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

struct Node {
    int day;
    long long height;
};

bool compareNodes(const Node& a, const Node& b) {
    return a.day < b.day;
}

int main() {
    int N;
    long long L;
    cin >> N >> L;

    vector<long long> A(N);
    vector<long long> B(N);
    for (int i = 0; i < N; i++) {
        cin >> A[i] >> B[i];
    }

    vector<long long> C(N);
    for (int i = 0; i < N; i++) {
        cin >> C[i];
    }

    priority_queue<Node> pq;
    for (int i = 0; i < N; i++) {
        pq.push({i, A[i] - B[i]});
    }

    long long height = 0;
    int day = 0;
    while (!pq.empty()) {
        Node node = pq.top();
        pq.pop();

        if (height + A[node.day] - B[node.day] >= L) {
            cout << node.day + 1 << endl;
            return 0;
        }

        height += A[node.day] - B[node.day];
        day++;

        for (int i = 0; i < N; i++) {
            if (C[i] >= height) {
                pq.push({i, C[i]});
            }
        }

        sort(pq.begin(), pq.end(), compareNodes);
    }

    cout << -1 << endl;

    return 0;
}
