#include <iostream>
#include <vector>
#include <algorithm>

struct Point {
    long long x, y;
};

bool cmp(const Point& a, const Point& b) {
    return a.y < b.y;
}

long long cross(const Point& a, const Point& b, const Point& c) {
    return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
}

int main() {
    int n;
    std::cin >> n;

    std::vector<Point> points(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> points[i].x >> points[i].y;
    }

    std::sort(points.begin(), points.end(), cmp);

    long long ans = 0;
    int i = 0, j = 0;
    while (i < n && j < n) {
        while (j < n && cross(points[i], points[i + 1], points[j]) <= 0) {
            ++j;
        }
        if (j - i >= 2) {
            long long area = 0;
            for (int k = i + 1; k < j; ++k) {
                area = std::max(area, cross(points[i], points[i + 1], points[k]));
            }
            ans = std::max(ans, area);
        }
        ++i;
    }

    std::cout << ans << std::endl;

    return 0;
}
