import java.util.Arrays;
import java.util.Comparator;
import java.util.Scanner;

public class Main {
    static class Point {
        long x, y;

        public Point(long x, long y) {
            this.x = x;
            this.y = y;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Point[] points = new Point[n];
        for (int i = 0; i < n; i++) {
            points[i] = new Point(scanner.nextLong(), scanner.nextLong());
        }
        Arrays.sort(points, Comparator.comparingLong(p -> p.y));
        long[] prefixSum = new long[n + 1];
        for (int i = 0; i < n; i++) {
            prefixSum[i + 1] = prefixSum[i] + points[i].x;
        }
        long min = Long.MAX_VALUE;
        for (int i = 0; i <= n; i++) {
            long left = prefixSum[i];
            long right = prefixSum[n] - prefixSum[i];
            long area = Math.abs(left) + Math.abs(right);
            min = Math.min(min, area);
        }
        System.out.println(Math.round(min));
    }
}
