import sys
from collections import deque

def bfs(sx, sy, ex, ey, dog):
    q = deque([(sx, sy, 0)])
    visited = {(sx, sy)}
    while q:
        x, y, cost = q.popleft()
        if (x, y) == (ex, ey):
            return cost
        for dx, dy in [(-2, 2), (0, 2), (2, 2)]:
            nx, ny = x + dx, y + dy
            if 1 <= nx <= W and 1 <= ny <= H and (nx, ny) not in visited:
                if (nx, ny) in dog:
                    q.append((nx, ny, cost + 2))
                else:
                    q.append((nx, ny, cost + 1))
                visited.add((nx, ny))
    return -1

def solve():
    global W, H, K, dog
    W, H, K = map(int, input().split())
    N = int(input())
    dog = set()
    for _ in range(N):
        x, y = map(int, input().split())
        dog.add((x, y))
    ans = 0
    for i in range(1, W + 1, 2):
        for j in range(1, H + 1, 2):
            if (i, j) not in dog:
                cost = bfs(i, j, i, j + 2, dog)
                if cost == -1:
                    return -1
                ans += cost
                K -= 1
    if K < 0:
        return -1
    return ans

print(solve())
