#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100000

double dp[N + 1][N + 1];

int main() {
    int R_PASS, R_RC, K;
    int i, j, k, t;
    double ans;

    scanf("%d %d %d", &R_PASS, &R_RC, &K);
    for (i = 0; i <= K; i++) {
        scanf("%d", &t);
        for (j = 0; j <= K; j++) {
            dp[i][j] = 1e30;
        }
        dp[i][0] = 0;
    }

    for (i = 1; i <= K; i++) {
        for (j = 0; j <= K; j++) {
            for (k = 0; k <= j; k++) {
                dp[i][j] = fmin(dp[i][j], dp[i - 1][j - k] + (double) t * (j - k) / (i * 1.0));
            }
        }
    }

    ans = 1e30;
    for (i = 0; i <= K; i++) {
        ans = fmin(ans, dp[K][i] * (R_RC - R_PASS - i) / (K * 1.0));
    }

    printf("%.6lf\n", ans);

    return 0;
}
