#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

double dp[1001][31][31];

void solve() {
    int R_PASS, R_RC, K;
    cin >> R_PASS >> R_RC >> K;

    vector<int> T(K + 1);
    for (int i = 0; i <= K; i++) {
        cin >> T[i];
    }

    for (int i = 0; i <= R_RC; i++) {
        for (int j = 0; j <= K; j++) {
            for (int k = 0; k <= K; k++) {
                dp[i][j][k] = 1e9;
            }
        }
    }

    dp[R_PASS][0][0] = 0;

    for (int i = R_PASS + 1; i <= R_RC; i++) {
        for (int j = 0; j <= K; j++) {
            for (int k = 0; k <= K; k++) {
                if (j + k > K) continue;
                dp[i][j][k] = min(dp[i][j][k], dp[i - 1][j][k] + T[j + k]);
                if (i == R_RC) continue;
                for (int l = 0; l <= K; l++) {
                    if (j + l > K) continue;
                    dp[i + 1][j + l][k] = min(dp[i + 1][j + l][k], dp[i][j][k] + T[j + l]);
                }
            }
        }
    }

    double ans = 1e9;
    for (int i = 0; i <= K; i++) {
        ans = min(ans, dp[R_RC][i][K - i]);
    }

    cout << fixed << setprecision(10) << ans << endl;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    solve();

    return 0;
}
