import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int R_PASS = scanner.nextInt();
        int R_RC = scanner.nextInt();
        int K = scanner.nextInt();
        int[] T = new int[K + 1];
        for (int i = 0; i <= K; i++) {
            T[i] = scanner.nextInt();
        }
        double ans = 0;
        for (int i = 1; i <= K; i++) {
            double p = 1.0 * (R_RC - R_PASS - i + 1) / (R_RC - R_PASS);
            double cost = 0;
            for (int j = 0; j <= i; j++) {
                cost += 1.0 * T[j] * C(i, j, p);
            }
            ans = Math.max(ans, cost);
        }
        System.out.printf("%.6f\n", ans);
    }

    public static double C(int n, int k, double p) {
        if (k > n) return 0;
        if (k == 0 || k == n) return 1;
        return C(n - 1, k - 1, p) + C(n - 1, k, p);
    }
}
