#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 10010

typedef struct {
    int r;
    char s[20];
} Node;

Node nodes[MAXN];

int eval(int num, char *str) {
    int len = strlen(str);
    int res = 0;
    for (int i = 0; i < len; i++) {
        res = (res * 10 + (str[i] - '0')) % MOD;
    }
    return (res * num) % MOD;
}

int eval(char *str) {
    int len = strlen(str);
    int res = 0;
    for (int i = 0; i < len; i++) {
        res = (res * 10 + (str[i] - '0')) % MOD;
    }
    return res;
}

int main() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d %s", &nodes[i].r, nodes[i].s);
    }
    int res = eval(nodes[0].s, nodes[0].r);
    for (int i = 1; i < n; i++) {
        res = (res + eval(nodes[i].s, nodes[i].r)) % MOD;
    }
    printf("%d\n", res);
    return 0;
}
