#include <iostream>
#include <string>
#include <vector>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long eval(const string& s) {
    long long res = 0;
    for (int i = 0; i < s.size(); i++) {
        if (s[i] == '*') {
            res = res * (s[i + 1] - '0') % MOD;
            i++;
        } else if (s[i] == '+') {
            res = (res + (s[i + 1] - '0')) % MOD;
            i++;
        } else if (s[i] == '-') {
            res = (res - (s[i + 1] - '0') + MOD) % MOD;
            i++;
        } else {
            res = res * 10 + (s[i] - '0');
        }
    }
    return res;
}

long long solve(const vector<pair<int, string>>& a) {
    long long res = 0;
    for (int i = 0; i < a.size(); i++) {
        string s = string(a[i].second.size() * a[i].first, a[i].second[0]);
        res = (res + eval(s)) % MOD;
    }
    return res;
}

int main() {
    int n;
    cin >> n;
    vector<pair<int, string>> a(n);
    for (int i = 0; i < n; i++) {
        int r;
        string s;
        cin >> r >> s;
        a[i] = {r, s};
    }
    cout << solve(a) << endl;
    return 0;
}
