import java.util.Scanner;

public class Main {
    static final int MOD = 1000000007;
    static final int MAX = 1000000000;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] r = new int[n];
        String[] s = new String[n];
        for (int i = 0; i < n; i++) {
            r[i] = scanner.nextInt();
            s[i] = scanner.next();
        }
        long ans = eval(r, s, 0, n - 1);
        System.out.println(ans % MOD);
    }

    static long eval(int[] r, String[] s, int l, int r) {
        if (l == r) {
            long ans = 0;
            for (int i = 0; i < r[l]; i++) {
                ans = ans * 10 + s[l].charAt(i) - '0';
            }
            return ans;
        }
        int mid = (l + r) / 2;
        long ans = 0;
        for (int i = l; i <= mid; i++) {
            long num = eval(r, s, i, mid);
            long temp = ans;
            ans = (ans + num) % MOD;
            if (temp > ans) {
                ans += MOD;
            }
        }
        for (int i = mid + 1; i <= r; i++) {
            long num = eval(r, s, mid + 1, i);
            long temp = ans;
            ans = (ans + num) % MOD;
            if (temp > ans) {
                ans += MOD;
            }
        }
        return ans;
    }
}
