MOD = 10**9 + 7

def eval(s):
    stack = []
    for i, c in enumerate(s):
        if c.isdigit():
            num = 0
            while i < len(s) and s[i].isdigit():
                num = num * 10 + int(s[i])
                i += 1
            stack.append(num)
        elif c in '+-':
            num2 = stack.pop()
            num1 = stack.pop()
            if c == '+':
                stack.append(num1 + num2)
            else:
                stack.append(num1 - num2)
        else:
            num2 = stack.pop()
            stack.append(stack.pop() * num2)
    return stack[0]

def solve():
    n = int(input())
    ans = 0
    for _ in range(n):
        r, s = input().split()
        r = int(r)
        ans = (ans + eval(s * int(r))) % MOD
    print(ans)

