#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define N 100000
#define INF 0x3f3f3f3f

int n, g[N], d[N], f[N], g1[N], g2[N], dis[N], vis[N];
int head[N], to[N], nxt[N], tot, mx;

void add(int x, int y, int z) {
    to[tot] = y;
    dis[tot] = z;
    nxt[tot] = head[x];
    head[x] = tot++;
}

void dfs(int x, int fa, int sum) {
    f[x] = sum;
    vis[x] = 1;
    for (int i = head[x]; i != -1; i = nxt[i]) {
        int y = to[i];
        if (y == fa) continue;
        dfs(y, x, sum + dis[i]);
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) scanf("%d", &g[i]);
    for (int i = 1; i < n; i++) {
        scanf("%d%d%d", &g1[i], &g2[i], &d[i]);
        add(g1[i], g2[i], d[i]);
        add(g2[i], g1[i], d[i]);
    }
    for (int i = 1; i <= n; i++) head[i] = -1;
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) dfs(i, 0, 0);
    }
    for (int i = 1; i <= n; i++) {
        for (int j = head[i]; j != -1; j = nxt[j]) {
            int y = to[j];
            if (f[y] > f[i]) f[i] = f[y];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = head[i]; j != -1; j = nxt[j]) {
            int y = to[j];
            if (f[y] > f[i]) f[i] = f[y];
        }
    }
    for (int i = 1; i <= n; i++) {
        mx = 0;
        for (int j = head[i]; j != -1; j = nxt[j]) {
            int y = to[j];
            if (f[y] > f[i]) continue;
            mx = (mx > f[y] + g[i] - dis[j]) ? mx : f[y] + g[i] - dis[j];
        }
        f[i] = mx;
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (ans > f[i]) ? ans : f[i];
    }
    printf("%d\n", ans + 1);
    return 0;
}
