#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, g[N], d[N], f[N], vis[N];
vector<int> e[N];

void dfs(int u, int fa, int dis) {
    f[u] = dis;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u, min(g[u], dis + d[u * N + v]));
    }
}

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) cin >> g[i];
    for (int i = 1; i < n; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        e[u].push_back(v);
        e[v].push_back(u);
        d[u * N + v] = d[v * N + u] = w;
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            dfs(i, 0, 0);
            int mx = 0;
            for (int j = 1; j <= n; j++) mx = max(mx, f[j]);
            ans = max(ans, mx);
        }
    }
    cout << ans << endl;
    return 0;
}
