import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] g;
    static int[][] e;
    static int[] d;
    static int[] dep;
    static int[] f;
    static int[] vis;
    static int[] st;
    static int top;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        g = new int[N];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            g[i] = Integer.parseInt(st.nextToken());
        }
        e = new int[N - 1][3];
        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            e[i][0] = Integer.parseInt(st.nextToken()) - 1;
            e[i][1] = Integer.parseInt(st.nextToken()) - 1;
            e[i][2] = Integer.parseInt(st.nextToken());
        }
        dep = new int[N];
        f = new int[N];
        vis = new int[N];
        st = new int[N];
        top = 0;
        Arrays.fill(f, Integer.MIN_VALUE);
        for (int i = 0; i < N; i++) {
            if (vis[i] == 0) {
                dfs(i, 0);
            }
        }
        int ans = 0;
        for (int i = 0; i < N; i++) {
            ans = Math.max(ans, dfs2(i, 0));
        }
        pw.println(ans);
        pw.close();
    }

    static void dfs(int u, int fa) {
        dep[u] = dep[fa] + 1;
        vis[u] = 1;
        for (int i = 0; i < N - 1; i++) {
            if (e[i][0] == u) {
                if (e[i][1] != fa) {
                    dfs(e[i][1], u);
                }
            } else if (e[i][1] == u) {
                if (e[i][0] != fa) {
                    dfs(e[i][0], u);
                }
            }
        }
    }

    static int dfs2(int u, int fa) {
        int res = f[u];
        for (int i = 0; i < N - 1; i++) {
            if (e[i][0] == u) {
                if (e[i][1] != fa) {
                    res = Math.max(res, dfs2(e[i][1], u) + g[e[i][1]]);
                }
            } else if (e[i][1] == u) {
                if (e[i][0] != fa) {
                    res = Math.max(res, dfs2(e[i][0], u) + g[e[i][0]]);
                }
            }
        }
        return res;
    }
}
