from collections import deque
import sys

def bfs(maze, start, goal, items, scores):
    queue = deque([(start, 0, 0, set())])
    visited = set()
    while queue:
        (x, y, dist, collected) = queue.popleft()
        if (x, y) == goal:
            return dist, max_score(collected, scores)
        if (x, y) in visited:
            continue
        visited.add((x, y))
        for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < len(maze) and 0 <= ny < len(maze[0]) and maze[nx][ny] != '#':
                if maze[nx][ny].isalpha():
                    item = maze[nx][ny].lower()
                    if item in collected:
                        if maze[nx][ny].islower():
                            queue.append(((nx, ny), dist + 1, collected, collected))
                        else:
                            queue.append(((nx, ny), dist + 1, collected, collected - {item}))
                    else:
                        if maze[nx][ny].islower():
                            queue.append(((nx, ny), dist + 1, collected, collected))
                        else:
                            queue.append(((nx, ny), dist + 1, collected, collected - {item}))
                else:
                    queue.append(((nx, ny), dist + 1, collected, collected))
    return -1, 0

def max_score(collected, scores):
    score = 0
    for i in range(len(scores)):
        if i in collected:
            for j in range(len(scores)):
                if j in collected and i < j:
                    score += scores[i][j]
    return score

def solve():
    w, h = map(int, input().split())
    maze = [list(input()) for _ in range(h)]
    start = None
    goal = None
    items = set()
    for i in range(h):
        for j in range(w):
            if maze[i][j] == 'S':
                start = (i, j)
            elif maze[i][j] == 'T':
                goal = (i, j)
            elif maze[i][j].isdigit():
                items.add(maze[i][j])
            elif maze[i][j].isalpha():
                items.add(maze[i][j].lower())
    scores = [list(map(int, input().split())) for _ in range(10)]
    dist, score = bfs(maze, start, goal, items, scores)
    print(dist, score)

solve()
