#include <stdio.h>
#include <stdlib.h>

#define N 105
#define M 21000

int n, m, cnt, head[N * N], nxt[M], to[M], vis[N * N], pos[N][N];
int tot, a[M], b[M];

void add(int x, int y) {
    nxt[++tot] = head[x];
    head[x] = tot;
    to[tot] = y;
}

void dfs(int u) {
    vis[u] = 1;
    for (int i = head[u]; i; i = nxt[i]) {
        int v = to[i];
        if (!vis[v]) {
            pos[v / n][v % n] = ++cnt;
            dfs(v);
        }
    }
}

int main() {
    scanf("%d", &n);
    m = 2 * n * n - 2 * n;
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &a[i], &b[i]);
        add(a[i], b[i]);
        add(b[i], a[i]);
    }
    dfs(1);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            printf("%d ", pos[i][j]);
        }
        printf("\n");
    }
    return 0;
}
