#include <iostream>
#include <vector>
#include <queue>
#include <utility>

using namespace std;

const int N = 105;

int n, m, cnt;
vector<int> e[N * N];
int vis[N * N], x[N * N], y[N * N];

void dfs(int u) {
    vis[u] = 1;
    for (int v : e[u]) {
        if (!vis[v]) {
            x[v] = x[u] + (v / n == u / n);
            y[v] = y[u] + (v % n == u % n);
            dfs(v);
        }
    }
}

int main() {
    cin >> n;
    m = n * n;
    for (int i = 1; i < m * 2 - m; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
        e[v].push_back(u);
    }
    for (int i = 1; i <= m; i++) {
        if (!vis[i]) {
            dfs(i);
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            cout << (i - 1) * n + j << " ";
        }
        cout << endl;
    }
    return 0;
}
