import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[][] adj;
    static int[][] pos;
    static boolean[] vis;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());

        adj = new int[2 * N * N - 2 * N + 1][2];
        pos = new int[N][N];
        vis = new boolean[2 * N * N - 2 * N + 1];

        for (int i = 1; i <= 2 * N * N - 2 * N; i++) {
            st = new StringTokenizer(br.readLine());
            adj[i][0] = Integer.parseInt(st.nextToken());
            adj[i][1] = Integer.parseInt(st.nextToken());
        }

        for (int i = 1; i <= N * N; i++) {
            if (!vis[i]) {
                dfs(i);
            }
        }

        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                pw.print(pos[i][j] + " ");
            }
            pw.println();
        }

        pw.close();
    }

    static void dfs(int u) {
        vis[u] = true;
        int x = (int) Math.floor((u - 1) / N);
        int y = (u - 1) % N;
        pos[x][y] = u;

        for (int i = 0; i < 2 * N * N - 2 * N; i++) {
            if (adj[i][0] == u && !vis[adj[i][1]]) {
                dfs(adj[i][1]);
            } else if (adj[i][1] == u && !vis[adj[i][0]]) {
                dfs(adj[i][0]);
            }
        }
    }
}
