    for (int i = 0; i < n; i++) {
        for (int j = 0; j < 3; j++) {
            for (int k = 0; k < 3; k++) {
                prob[i][j][k][0] = 0;
                prob[i][j][k][1] = 0;
                prob[i][j][k][2] = 0;
            }
        }
    }
}

