#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);

    int sum_w = 0;
    int sum_b = 0;
    int sum_diff = 0;

    for (int i = 0; i < N; i++) {
        int w, b;
        scanf("%d %d", &w, &b);
        sum_w += w;
        sum_b += b;
        sum_diff += w - b;
    }

    if (sum_diff <= 0) {
        printf("0\n");
    } else {
        printf("1\n");
    }

    return 0;
}
