#include <stdio.h>
#include <string.h>
#include <limits.h>

#define N 55

char grid[N][N];
int dp[N][N][2];

int dfs(int i, int j, int dir) {
    if (i == N - 1 && j == N - 1) return 0;
    if (dp[i][j][dir] != -1) return dp[i][j][dir];
    int res = 0;
    if (dir == 0) {
        if (i + 1 < N) res = max(res, dfs(i + 1, j, 0) + (grid[i + 1][j] - '0'));
        if (j + 1 < N) res = max(res, dfs(i, j + 1, 1) + (grid[i][j + 1] - '0'));
        if (i + 1 < N && j + 1 < N) {
            int val = grid[i + 1][j] - '0';
            if (val == 1) val = 10;
            if (val == 2) val = 100;
            if (val == 3) val = 1000;
            if (val == 4) val = 10000;
            if (val == 5) val = 100000;
            if (val == 6) val = 1000000;
            if (val == 7) val = 10000000;
            if (val == 8) val = 100000000;
            if (val == 9) val = 1000000000;
            res = max(res, dfs(i + 1, j + 1, 0) + val);
        }
    } else {
        if (i + 1 < N) res = max(res, dfs(i + 1, j, 0) + (grid[i + 1][j] == '*' ? 2 : 0));
        if (j + 1 < N) res = max(res, dfs(i, j + 1, 1) + (grid[i][j + 1] == '*' ? 2 : 0));
        if (i + 1 < N && j + 1 < N) res = max(res, dfs(i + 1, j + 1, 0) + (grid[i + 1][j] == '*' ? 2 : 0) + (grid[i][j + 1] == '*' ? 2 : 0));
    }
    return dp[i][j][dir] = res;
}

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    for (int i = 0; i < H; i++) {
        scanf("%s", grid[i]);
        for (int j = 0; j < W; j++) {
            grid[i][j] = grid[i][j * 2];
        }
    }
    memset(dp, -1, sizeof(dp));
    int res = dfs(0, 0, 0);
    if (res >= 1e15) res = -1;
    printf("%d\n", res);
    return 0;
}
