#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 55;
const long long INF = 1e15;

long long f[N][N][2];
char g[N][N];
int h, w;

long long dfs(int x, int y, int t) {
    if (x == h && y == w) return 0;
    if (f[x][y][t] != -1) return f[x][y][t];
    long long res = -INF;
    if (t == 0) {
        if (x + 1 <= h) res = max(res, dfs(x + 1, y, 0) + g[x + 1][y] - '0');
        if (y + 1 <= w) res = max(res, dfs(x, y + 1, 0) + g[x][y + 1] - '0');
    }
    else {
        if (x + 1 <= h) res = max(res, dfs(x + 1, y, 1) + (g[x + 1][y] == '*' ? 1 : 0));
        if (y + 1 <= w) res = max(res, dfs(x, y + 1, 1) + (g[x][y + 1] == '*' ? 1 : 0));
    }
    if (t == 0) {
        if (x + 1 <= h && y + 1 <= w) res = max(res, dfs(x + 1, y + 1, 1) + g[x + 1][y + 1] - '0');
    }
    else {
        if (x + 1 <= h && y + 1 <= w) res = max(res, dfs(x + 1, y + 1, 0) + (g[x + 1][y + 1] == '*' ? 1 : 0));
    }
    return f[x][y][t] = res;
}

int main() {
    cin >> h >> w;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            cin >> g[i][j];
        }
    }
    for (int i = 0; i <= h; i++) {
        for (int j = 0; j <= w; j++) {
            f[i][j][0] = f[i][j][1] = -1;
        }
    }
    long long res = dfs(1, 1, 0);
    if (res >= INF) cout << -1 << endl;
    else cout << res << endl;
    return 0;
}
