#include <iostream>
#include <vector>
#include <queue>
#include <unordered_set>

using namespace std;

int main() {
    int N, M;
    cin >> N >> M;

    vector<vector<int>> graph(N);
    for (int i = 0; i < M; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        graph[u].push_back(v);
        graph[v].push_back(u);
    }

    vector<int> parent(N, -1);
    queue<int> q;
    q.push(0);
    parent[0] = -2;

    int level = 0;
    unordered_set<int> visited;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; i++) {
            int u = q.front();
            q.pop();
            visited.insert(u);
            for (int v : graph[u]) {
                if (parent[v] == -1) {
                    parent[v] = u;
                    q.push(v);
                }
            }
        }
        level++;
    }

    int maxLevel = 0;
    for (int i = 0; i < N; i++) {
        maxLevel = max(maxLevel, parent[i] + 1);
    }

    if (maxLevel == N) {
        cout << level << endl;
    } else {
        cout << -1 << endl;
    }

    return 0;
}
