from collections import deque

def bfs(n, edges):
    graph = [[] for _ in range(n + 1)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = set()
    current = [1]
    iteration = 0
    while True:
        iteration += 1
        next_level = set()
        for node in current:
            if node not in visited:
                visited.add(node)
                next_level.update(graph[node])
        if len(next_level) == 0:
            return iteration
        current = list(next_level)

def main():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))
    result = bfs(n, edges)
    if result == float('inf'):
        print(-1)
    else:
        print(result)

if __name__ == "__main__":
    main()
