#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 12, M = 12, K = 6, S = 1000001;

int n, m, k, s;
int a[N][M];
int dp[N][1 << M];

void solve() {
    cin >> n >> m >> k >> s;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            cin >> a[i][j];
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            dp[i][j] = S;
        }
    }
    dp[0][0] = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < (1 << m); j++) {
            for (int x = 0; x < m; x++) {
                if (j & (1 << x)) {
                    dp[i + 1][j ^ (1 << x)] = min(dp[i + 1][j ^ (1 << x)], dp[i][j] + a[i][x]);
                }
            }
        }
    }
    for (int i = 0; i < (1 << m); i++) {
        if (dp[n][i] <= s) {
            cout << "Yes" << endl;
            return;
        }
    }
    cout << "No" << endl;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    solve();
    return 0;
}
