#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100
#define INF 1e18

typedef struct {
    double x, y;
} Point;

int n, h, w, r;
Point p[N];

double dist(Point a, Point b) {
    return sqrt(pow(a.x - b.x, 2) + pow(a.y - b.y, 2));
}

int check(Point *p, int n, double R) {
    double minx = INF, maxx = -INF, miny = INF, maxy = -INF;
    for (int i = 0; i < n; i++) {
        minx = fmin(minx, p[i].x - R);
        maxx = fmax(maxx, p[i].x + R);
        miny = fmin(miny, p[i].y - R);
        maxy = fmax(maxy, p[i].y + R);
    }
    return (minx >= -R && maxx <= w + R && miny >= -R && maxy <= h + R);
}

int main() {
    scanf("%d %d %d %d", &n, &h, &w, &r);
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf", &p[i].x, &p[i].y);
    }
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            double d = dist(p[i], p[j]);
            if (d > 2 * r) {
                printf("No\n");
                return 0;
            }
        }
    }
    printf("Yes\n");
    return 0;
}
