#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

struct Point {
    long long x, y;
};

bool check(vector<Point>& points, long long h, long long w, long long r) {
    long long n = points.size();
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            long long dx = points[j].x - points[i].x;
            long long dy = points[j].y - points[i].y;
            long long d = sqrt(dx * dx + dy * dy);
            if (d > w + 2 * r || dy > h + 2 * r) {
                return false;
            }
        }
    }
    return true;
}

int main() {
    int n;
    long long h, w, r;
    cin >> n >> h >> w >> r;
    vector<Point> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].x >> points[i].y;
    }
    sort(points.begin(), points.end(), [](const Point& a, const Point& b) {
        return a.x * a.x + a.y * a.y < b.x * b.x + b.y * b.y;
    });
    for (int i = 0; i < n; i++) {
        points[i].x = min(max(points[i].x, -r), w + r);
        points[i].y = min(max(points[i].y, -r), h + r);
    }
    if (check(points, h, w, r)) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }
    return 0;
}
