#include <stdio.h>
#include <stdlib.h>

#define N 100000
#define C 100

int main() {
    int n, c, i, j, k, l, r, a[N + 1], w[N + 1], b[N + 1], p[N + 1], sum[N + 1], ans = 0;

    scanf("%d%d", &n, &c);
    for (i = 1; i <= n; i++) {
        scanf("%d%d", &b[i], &w[i]);
        p[b[i]] = i;
    }

    for (i = 1; i <= n; i++) {
        a[i] = p[i];
        sum[i] = sum[i - 1] + w[a[i]];
    }

    for (i = 1; i <= n; i++) {
        l = 1;
        r = i - 1;
        while (l <= r) {
            j = (l + r) / 2;
            if (a[j] < i) {
                l = j + 1;
            } else {
                r = j - 1;
            }
        }
        k = l;
        while (k <= i - 1) {
            if (a[k] == i) {
                break;
            }
            k++;
        }
        ans += sum[n] - sum[i - 1] - (sum[i - 1] - sum[k - 1]) + c * (i - k) * w[i];
    }

    printf("%d\n", ans);
    return 0;
}
