#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 45

int n, m, k, q;
int o[MAXN], x[MAXN];

int main() {
    scanf("%d %d %d %d", &n, &m, &k, &q);
    for (int i = 0; i < n; i++) {
        scanf("%d", &o[i]);
    }
    for (int i = 0; i < q; i++) {
        scanf("%d", &x[i]);
    }
    for (int i = 0; i < q; i++) {
        int l = 0, r = n - 1;
        while (l < r) {
            int mid = (l + r) / 2;
            if (o[mid] > x[i]) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (o[l] == x[i]) {
            printf("%d", l + 1);
            for (int j = l + 1; j < n; j++) {
                printf(" %d", j + 1);
            }
            printf("\n");
        } else {
            printf("-1\n");
        }
    }
    return 0;
}
