#include <iostream>
#include <vector>
#include <algorithm>
#include <string>

using namespace std;

int main() {
    int N, M, K, Q;
    cin >> N >> M >> K >> Q;

    vector<int> O(N);
    for (int i = 0; i < N; i++) {
        cin >> O[i];
    }

    vector<int> X(Q);
    for (int i = 0; i < Q; i++) {
        cin >> X[i];
    }

    vector<vector<int>> ans(Q);

    for (int i = 0; i < Q; i++) {
        int x = X[i];
        int l = 0;
        int r = N - 1;
        int res = -1;
        while (l <= r) {
            int mid = (l + r) / 2;
            if (O[mid] <= x) {
                res = mid;
                l = mid + 1;
            } else {
                r = mid - 1;
            }
        }

        if (res == -1) {
            ans[i].push_back(-1);
            continue;
        }

        int sum = 0;
        for (int j = 0; j < res; j++) {
            sum += O[j];
        }

        if (sum == x) {
            for (int j = 0; j < res; j++) {
                ans[i].push_back(j + 1);
            }
        } else {
            int diff = x - sum;
            int l = 0;
            int r = M - 1;
            int res2 = -1;
            while (l <= r) {
                int mid = (l + r) / 2;
                if (diff % (mid + 1) == 0 && diff / (mid + 1) < O[res]) {
                    res2 = mid;
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            }

            if (res2 == -1) {
                ans[i].push_back(-1);
            } else {
                ans[i].push_back(res + 1);
                for (int j = 0; j < res2; j++) {
                    ans[i].push_back(j + 1);
                }
            }
        }
    }

    for (int i = 0; i < Q; i++) {
        for (int j = 0; j < ans[i].size(); j++) {
            cout << ans[i][j] << " ";
        }
        cout << endl;
    }

    return 0;
}
