import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }

    static class Pair implements Comparable<Pair> {
        int first;
        int second;

        public Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Integer.compare(this.first, o.first);
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        int q = sc.nextInt();
        int[] o = new int[n];
        for (int i = 0; i < n; i++) {
            o[i] = sc.nextInt();
        }
        int[] x = new int[q];
        for (int i = 0; i < q; i++) {
            x[i] = sc.nextInt();
        }
        int[] ans = new int[q];
        for (int i = 0; i < q; i++) {
            int sum = x[i];
            int l = 0;
            int r = n - 1;
            int idx = -1;
            while (l <= r) {
                int mid = (l + r) / 2;
                if (o[mid] <= sum) {
                    idx = mid;
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            }
            if (idx == -1) {
                ans[i] = -1;
                continue;
            }
            int[] dp = new int[k + 1];
            dp[0] = 1;
            for (int j = 0; j < n; j++) {
                if (j == idx) {
                    continue;
                }
                for (int p = k; p >= o[j]; p--) {
                    dp[p] |= dp[p - o[j]];
                }
            }
            boolean found = false;
            for (int j = 0; j <= k; j++) {
                if (dp[j] == 1 && j <= sum) {
                    ans[i] = idx + 1;
                    found = true;
                    break;
                }
            }
            if (!found) {
                ans[i] = -1;
            }
        }
        for (int i = 0; i < q; i++) {
            out.print(ans[i] + " ");
        }
        out.println();
        out.close();
    }
}
