#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 100000
#define Q 100000
#define MD 1000000007

int n, q;
int p[N + 1], pp[N + 1], cnt[N + 1], vis[N + 1];
int a[N + 1], b[N + 1], ans[N + 1];

void dfs(int i) {
    if (vis[i]) return;
    vis[i] = 1;
    dfs(p[i]);
    cnt[i]++;
}

void init() {
    for (int i = 1; i <= n; i++) {
        p[i] = i;
        cnt[i] = 1;
        vis[i] = 0;
    }
    for (int i = 1; i <= n; i++) dfs(i);
}

void swap(int i, int j) {
    int t = p[i];
    p[i] = p[j];
    p[j] = t;
}

int main() {
    scanf("%d%d", &n, &q);
    init();
    for (int i = 1; i <= q; i++) {
        scanf("%d%d", &a[i], &b[i]);
        swap(a[i], b[i]);
    }
    for (int i = 1; i <= q; i++) {
        int x = a[i], y = b[i];
        while (p[x] != x) {
            pp[x] = p[x];
            x = p[x];
        }
        while (p[y] != y) {
            pp[y] = p[y];
            y = p[y];
        }
        if (x == y) {
            ans[i] = 1;
            continue;
        }
        int k = 0;
        while (pp[x] != x) {
            k++;
            int t = pp[x];
            pp[x] = x;
            x = t;
        }
        while (pp[y] != y) {
            k++;
            int t = pp[y];
            pp[y] = y;
            y = t;
        }
        ans[i] = k + 1;
    }
    for (int i = 1; i <= q; i++) {
        printf("%d\n", ans[i]);
    }
    return 0;
}
