#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

const int N = 1e5 + 5;
const int mod = 1e9 + 7;

int n, q;
int p[N], cnt[N], vis[N], id[N], dep[N], ans[N];
vector<int> e[N];

void dfs(int u) {
    if (vis[u]) return;
    vis[u] = 1;
    id[u] = ++cnt[dep[u]];
    dep[u] = dep[p[u]];
    dfs(p[u]);
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n >> q;
    for (int i = 1; i <= n; i++) {
        p[i] = i;
        e[i].clear();
    }
    for (int i = 1; i <= q; i++) {
        int x, y;
        cin >> x >> y;
        e[x].push_back(y);
        e[y].push_back(x);
    }
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            dfs(i);
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j : e[i]) {
            if (id[i] < id[j]) {
                swap(p[id[i]], p[id[j]]);
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (p[i] == i) {
            int t = 0;
            for (int j = 1; j <= n; j++) {
                if (p[j] != j) {
                    t = (t * 2 + 1) % mod;
                }
            }
            ans[cnt[i]] = (ans[cnt[i]] + t) % mod;
        }
    }
    for (int i = q; i >= 1; i--) {
        int x, y;
        cin >> x >> y;
        if (id[x] < id[y]) {
            swap(x, y);
        }
        for (int j = 1; j <= n; j++) {
            if (p[j] == x) {
                p[j] = y;
            } else if (p[j] == y) {
                p[j] = x;
            }
        }
        for (int j = 1; j <= n; j++) {
            if (p[j] != j) {
                dep[j] = dep[p[j]];
                id[j] = ++cnt[dep[j]];
            }
        }
        ans[cnt[x]] = (ans[cnt[x]] - ans[cnt[y]] + mod) % mod;
        cout << (ans[cnt[x]] + mod) % mod << '\n';
    }
    return 0;
}
