#include <iostream>
#include <string>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

int dfs(const string& s, map<char, int>& cnt, map<char, int>& win) {
    if (s[0] >= 'a' && s[0] <= 'z') {
        return 1;
    }
    int res = 0;
    for (int i = 1; i < s.size(); i++) {
        if (s[i] == '-') {
            res += dfs(s.substr(1, i - 1), cnt, win);
            res += dfs(s.substr(i + 1), cnt, win);
            return res;
        }
    }
    return 1;
}

int main() {
    string s;
    cin >> s;
    int n;
    cin >> n;
    vector<pair<char, int>> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i].first >> a[i].second;
    }
    map<char, int> cnt;
    map<char, int> win;
    for (int i = 0; i < n; i++) {
        cnt[a[i].first]++;
        win[a[i].first] = a[i].second;
    }
    int res = dfs(s, cnt, win);
    for (auto& p : win) {
        if (p.second > cnt[p.first]) {
            res = 0;
            break;
        }
    }
    for (auto& p : cnt) {
        if (p.second % 2 == 1) {
            res = 0;
            break;
        }
    }
    if (res == 0) {
        cout << "No" << endl;
    } else {
        cout << "Yes" << endl;
    }
    return 0;
}
