import java.util.*;
import java.io.*;

public class Main {
    static int N = 26;
    static int INF = (int) 1e9;

    static int dfs(char[][] graph, int u, int[] in, int[] out, int[] cnt) {
        if (in[u] != -1) return in[u];
        if (out[u] != -1) return out[u];
        int res = 1;
        for (int v : graph[u]) {
            res += dfs(graph, v, in, out, cnt);
        }
        if (res > N) return out[u] = -1;
        cnt[res]++;
        return in[u] = res;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        char[][] graph = new char[N][];
        int[] in = new int[N];
        int[] out = new int[N];
        Arrays.fill(in, -1);
        Arrays.fill(out, -1);
        int[] cnt = new int[N + 1];
        String s = br.readLine();
        s = s.substring(2, s.length() - 2);
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '-') {
                int u = s.charAt(i - 1) - 'a';
                int v = s.charAt(i + 1) - 'a';
                graph[u] = Arrays.copyOf(graph[u], graph[u].length + 1);
                graph[u][graph[u].length - 1] = v;
                graph[v] = Arrays.copyOf(graph[v], graph[v].length + 1);
                graph[v][graph[v].length - 1] = u;
            }
        }
        for (int i = 0; i < N; i++) {
            if (graph[i].length == 0) {
                graph[i] = new char[]{i};
            }
        }
        for (int i = 0; i < N; i++) {
            dfs(graph, i, in, out, cnt);
        }
        for (int i = 1; i <= N; i++) {
            if (cnt[i] > 1) {
                System.out.println("No");
                return;
            }
        }
        System.out.println("Yes");
    }
}
