#include <stdio.h>
#include <stdlib.h>

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) return 0;
    }
    return 1;
}

int count_prime_factors(int n) {
    int count = 0;
    for (int i = 2; i * i <= n; i++) {
        while (n % i == 0) {
            n /= i;
            count++;
        }
    }
    if (n > 1) count++;
    return count;
}

int is_prime_factor_prime(int n) {
    return is_prime(count_prime_factors(n));
}

int main() {
    int l, r;
    scanf("%d %d", &l, &r);
    int count = 0;
    for (int i = l; i <= r; i++) {
        if (is_prime_factor_prime(i)) count++;
    }
    printf("%d\n", count);
    return 0;
}
