#include <iostream>
#include <vector>
#include <cmath>

bool isPrime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return false;
    return true;
}

int countPrimeFactors(int n) {
    int count = 0;
    for (int i = 2; i * i <= n; ++i) {
        while (n % i == 0) {
            n /= i;
            ++count;
        }
    }
    if (n > 1) ++count;
    return count;
}

int main() {
    int l, r;
    std::cin >> l >> r;
    int ans = 0;
    for (int i = l; i <= r; ++i) {
        if (isPrime(countPrimeFactors(i))) ++ans;
    }
    std::cout << ans << std::endl;
    return 0;
}
