#include <stdio.h>
#include <stdlib.h>

#define N 100000
#define K 100000

int main() {
    int n, k, i, j, a[N + 1], b[N + 1], c[N + 1], x, y, z;

    scanf("%d %d", &n, &k);
    for (i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }

    for (i = 1; i <= n; i++) {
        b[i] = a[i];
    }

    for (i = 2; i <= n; i++) {
        for (j = 1; j < i; j++) {
            b[i] ^= b[j];
        }
    }

    x = 0;
    y = 0;
    for (i = 1; i <= n; i++) {
        z = (n + 1) * i - i * (i + 1) / 2;
        if (k <= z) {
            x = i;
            y = k;
            break;
        }
    }

    for (i = 1; i <= x; i++) {
        c[i] = b[i];
    }

    for (i = x + 1; i <= n; i++) {
        c[i] = c[i - x] ^ a[i];
    }

    for (i = x; i >= 1; i--) {
        if (y <= i) {
            break;
        }
        y -= i;
        c[i] = c[i - 1] ^ c[i];
    }

    printf("%d\n", c[x] ^ c[x - 1]);

    return 0;
}
