#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 100000
#define M 200000

typedef struct {
    int to, next, c;
} edge;

edge e[M * 2 + 1];
int head[N + 1], tot;

void add_edge(int u, int v, int c) {
    e[++tot] = (edge) {v, head[u], c};
    head[u] = tot;
}

int dis[N + 1], vis[N + 1];

void dijkstra(int s, int t) {
    for (int i = 1; i <= N; i++) {
        dis[i] = INT_MAX;
        vis[i] = 0;
    }
    dis[s] = 0;
    for (int i = 1; i <= N; i++) {
        int u = 0;
        for (int j = 1; j <= N; j++) {
            if (!vis[j] && (u == 0 || dis[j] < dis[u])) {
                u = j;
            }
        }
        vis[u] = 1;
        for (int j = head[u]; j; j = e[j].next) {
            int v = e[j].to;
            if (e[j].c && dis[v] > dis[u] + e[j].c) {
                dis[v] = dis[u] + e[j].c;
            }
        }
    }
}

int main() {
    int n, m, s, t;
    scanf("%d%d%d%d", &n, &m, &s, &t);
    for (int i = 1; i <= m; i++) {
        int u, v, c;
        scanf("%d%d%d", &u, &v, &c);
        add_edge(u, v, c);
        add_edge(v, u, c);
    }
    dijkstra(s, t);
    if (dis[t] == INT_MAX) {
        printf("-1\n");
    } else {
        dijkstra(t, s);
        for (int i = 1; i <= n; i++) {
            if (i != s && i != t && dis[i] != INT_MAX) {
                add_edge(i, t, dis[i]);
            }
        }
        dijkstra(s, t);
        printf("%d\n", dis[t]);
    }
    return 0;
}
