#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int MAXN = 1e5 + 5;

struct Edge {
    int to, next, w;
};

Edge e[MAXN * 2];
int head[MAXN], tot;
long long dis[MAXN], vis[MAXN];
int n, m, s, t;

void addEdge(int u, int v, int w) {
    e[++tot] = {v, head[u], w};
    head[u] = tot;
}

void dijkstra(int s) {
    for (int i = 1; i <= n; i++) {
        dis[i] = numeric_limits<long long>::max();
        vis[i] = 0;
    }
    dis[s] = 0;
    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<pair<long long, int>>> q;
    q.push({0, s});
    while (!q.empty()) {
        int u = q.top().second;
        q.pop();
        if (vis[u]) continue;
        vis[u] = 1;
        for (int i = head[u]; i; i = e[i].next) {
            int v = e[i].to;
            if (e[i].w > 0) {
                if (!vis[v] && dis[v] > dis[u] + e[i].w) {
                    dis[v] = dis[u] + e[i].w;
                    q.push({dis[v], v});
                }
            }
        }
    }
}

int main() {
    cin >> n >> m >> s >> t;
    for (int i = 1; i <= m; i++) {
        int u, v, w;
        cin >> u >> v >> w;
        addEdge(u, v, w);
        addEdge(v, u, w);
    }
    dijkstra(s);
    if (dis[t] == numeric_limits<long long>::max()) {
        cout << -1 << endl;
    } else {
        dijkstra(t);
        long long ans = numeric_limits<long long>::max();
        for (int i = 1; i <= n; i++) {
            if (i != t && dis[i] != numeric_limits<long long>::max()) {
                ans = min(ans, dis[i]);
            }
        }
        cout << ans << endl;
    }
    return 0;
}
