import heapq
import sys

N, M, S, T = map(int, input().split())
edges = []
for _ in range(M):
    u, v, c = map(int, input().split())
    edges.append((u, v, c))
    edges.append((v, u, c))

edges.sort(key=lambda x: x[2])

parent = list(range(N + 1))
rank = [0] * (N + 1)

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    root_x = find(x)
    root_y = find(y)
    if root_x != root_y:
        if rank[root_x] > rank[root_y]:
            parent[root_y] = root_x
        else:
            parent[root_x] = root_y
            if rank[root_x] == rank[root_y]:
                rank[root_y] += 1

def dijkstra(s):
    dist = [float('inf')] * (N + 1)
    dist[s] = 0
    pq = [(0, s)]
    while pq:
        d, u = heapq.heappop(pq)
        if d > dist[u]:
            continue
        for v, c in edges:
            if v == u:
                v2 = v
            elif v2 == u:
                v2 = v
            else:
                continue
            if find(u) != find(v2):
                continue
            if dist[v2] > dist[u] + c:
                dist[v2] = dist[u] + c
                heapq.heappush(pq, (dist[v2], v2))
    return dist

def solve():
    global edges
    dist1 = dijkstra(S)
    dist2 = dijkstra(T)
    for i in range(1, N + 1):
        for j in range(1, N + 1):
            if find(i) != find(j):
                union(i, j)
    edges.sort(key=lambda x: x[2], reverse=True)
    for u, v, c in edges:
        if find(u) != find(v):
            continue
        if dist1[u] + c < dist1[v]:
            dist1[v] = dist1[u] + c
        if dist2[u] + c < dist2[v]:
            dist2[v] = dist2[u] + c
        union(u, v)
    if dist1[T] == float('inf'):
        return -1
    return min(dist1[T] + dist2[T], min(dist1[T], dist2[T]))

print(solve())
