#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, k;
vector<int> e[N];
int sz[N], dp[N], ans;

void dfs(int u, int fa) {
    sz[u] = 1;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u);
        sz[u] += sz[v];
    }
}

void dfs2(int u, int fa, int sum) {
    dp[u] = sum;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs2(v, u, sum + sz[v]);
    }
}

void dfs3(int u, int fa, int sum) {
    ans = max(ans, dp[u] + sum);
    for (int v : e[u]) {
        if (v == fa) continue;
        if (sz[v] >= k) dfs3(v, u, sum + sz[v]);
    }
}

int main() {
    cin >> n >> k;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
        e[v].push_back(u);
    }
    dfs(1, 0);
    dfs2(1, 0, 0);
    dfs3(1, 0, 0);
    cout << n - ans << endl;
    return 0;
}
