#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 41
#define MAX_W 41

int h, w;
char map[MAX_H][MAX_W];
int visited[MAX_H][MAX_W];
int city_count;
int start_x, start_y;
int end_x, end_y;

void dfs(int x, int y) {
    if (x < 0 || x >= h || y < 0 || y >= w) return;
    if (visited[x][y]) return;
    visited[x][y] = 1;
    if (map[x][y] == '*') city_count++;
    if (map[x][y] == '@') {
        start_x = x;
        start_y = y;
    }
    if (map[x][y] == '*') {
        end_x = x;
        end_y = y;
    }
    if (map[x][y] == '#') {
        map[x][y] = '#';
        dfs(x - 1, y);
        dfs(x + 1, y);
        dfs(x, y - 1);
        dfs(x, y + 1);
    }
}

void print_map() {
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            printf("%c", map[i][j]);
        }
        printf("\n");
    }
}

int main() {
    int i, j;
    scanf("%d %d", &h, &w);
    for (i = 0; i < h; i++) {
        scanf("%s", map[i]);
    }
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (map[i][j] == '@') {
                start_x = i;
                start_y = j;
            }
            if (map[i][j] == '*') {
                dfs(i, j);
            }
        }
    }
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (map[i][j] == '.') {
                map[i][j] = '#';
                dfs(i, j);
                if (city_count > 0) {
                    city_count = 0;
                } else {
                    map[i][j] = '.';
                }
            }
        }
    }
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (map[i][j] == '#') {
                if (i == start_x && j == start_y) {
                    map[i][j] = '@';
                } else if (i == end_x && j == end_y) {
                    map[i][j] = '*';
                }
            }
        }
    }
    print_map();
    return 0;
}
